  // function showAnswers(id,query,tags){
  //   axios.get(`/getanswers?id=${id}`).then(res => {
  //       if(res){
  //         setAnswers(res.data);
  //         setQuery(query);
  //         setTags(tags);
  //         setQuestionID(id);
  //       }
  //   }).catch(err => {
  //       console.log(err);
  //   });
