  //         window.location.reload();
  //       }
  //     })
  //     .catch(err => {
  //       console.log(err);
  //     });
  //   }else{
  //     toast("Something Went Wrong!!");
  //   }
  //   setShowAnswer(false);
  // }

  const getExperience = (limit, skip) => {
    axios
      .get(`/getallexperiences?limit=${limit}&skip=${skip}`)
      .then((res) => {
        if (res) {
          setExperience(res.data);
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div className={`homeContainer ${darkMode === "light" ? "light" : "dark"}`}>
      <Header />
      <ToastContainer
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
      />

      <div className="container">
        <div className="searchFilter">
          <h4
            className={`totalQues  ${darkMode === "light" ? "light" : "dark"}`}
          >
           {/* Total Experience: {experience.length}*/}
          </h4>
          <div className="d-flex justify-content-center">
            <button
              className="btn btn-primary btn-sm mt-2"
              onClick={previousPage}
            >
              {" "}
              Previous Page{" "}
            </button>
            <button
              className="btn btn-primary btn-sm mt-2 ms-2"
              onClick={nextPage}
            >
              {" "}
              Next Page{" "}
            </button>
          </div>
        </div>
        {experience.length > 0 ? (
          experience.map((experience, index) => {
            return (
              <div className="homequery shadow-sm" key={index}>
                <h3> {experience.expname}</h3>
                <div
                  dangerouslySetInnerHTML={{ __html: experience.query }}
                />{" "}
                <br></br>
                <div className="footer">
                  <strong>Batch:</strong> {experience.year} <br></br>
                  <strong> LinkedIn: </strong>{experience.authorlinkedin}
                  <br></br>
                  <strong>Email: </strong>{experience.authoremail}
                  <br></br>
                  <strong>Contact: </strong>{experience.authorcontact}
                </div>
                <div className="footer">
                  Shared by{" "}

                  <strong>  {experience.author}</strong>
                  {" "}
                  {moment(experience.createdAt).fromNow()} works in{" "}

                    {experience.company}
                  {" "}

                </div>
              </div>
            );
          })
        ) : (
          <div className="notFound">No Experience Found</div>
        )}
      </div>
    </div>
  );
}

export default Experience;
